// YourComponent.vue
<template>
    <div>
        <h1>Hot Stocks</h1>
        <ul>
            <li v-for="stock in hotStocks" :key="stock.stock_symbol">
                {{ stock.stock_symbol }} - {{ stock.company_name }}
                <!-- Add other fields as needed -->
            </li>
        </ul>
    </div>
</template>

<script>
import axios from 'axios';

export default {
  data() {
    return {
      hotStocks: [],
    };
  },
  mounted() {
    this.fetchHotStocks();
  },
  methods: {
    fetchHotStocks() {
      axios.get('http://localhost:8000/api/hot_stocks/')
        .then(response => {
          this.hotStocks = response.data;
        })
        .catch(error => {
          console.error('Error fetching hot stocks:', error);
        });
    },
  },
};
</script>
